sourcesContent:
import React, { Component } from "react";
import { withRouter, Link } from "react-router-dom";
import styled from "styled-components";
import api from "../utils/ServicesBurung";
import api2 from "../utils/ServicesBreeding";
import assets from "../assets/f.png";

const Container = styled.nav`
  .jumbotron {
    /* background-image: url("https://i.pinimg.com/originals/6b/20/16/6b201623685e7093fe7df8970b1d26b5.jpg"); */
    background-image: url("f.png");
    background-size: cover;
  }
  .table {
    border-radius: 5px;
    width: 100%;
    margin: 0px auto;
    float: none;
  ]
      @media (max-width: 760px),
        (min-device-width: 768px) and (max-device-width: 1024px) {
        /* Force table to not be like tables anymore */
        table,
        thead,
        tbody,
        th,
        td,
        tr {
          display: block;
        }

        /* Hide table headers (but not display: none;, for accessibility) */
        thead tr {
          position: absolute;
          top: -9999px;
          left: -9999px;
        }

        tr {
          border: 1px solid #ccc;
        }

        td {
          /* Behave  like a "row" */
          border: none;
          border-bottom: 1px solid #eee;
          position: relative;
          padding-left: 50%;
        }

        td:before {
          /* Now like a table header */
          position: absolute;
          /* Top/left values mimic padding */
          top: 6px;
          left: 6px;
          width: 45%;
          padding-right: 10px;
          white-space: nowrap;
        }
      }
    }
  }

  .card-counter {
    box-shadow: 2px 2px 10px #dadada;
    margin: 5px;
    padding: 20px 10px;
    background-color: #fff;
    height: 100px;
    border-radius: 5px;
    transition: 0.3s linear all;
  }

  .card-counter:hover {
    box-shadow: 4px 4px 20px #dadada;
    transition: 0.3s linear all;
  }

  .card-counter.primary {
    background-color: #007bff;
    color: #fff;
  }

  .card-counter.danger {
    background-color: #ef5350;
    color: #fff;
  }

  .card-counter.success {
    background-color: #66bb6a;
    color: #fff;
  }

  .card-counter.info {
    background-color: #26c6da;
    color: #fff;
  }

  .card-counter i {
    font-size: 5em;
    opacity: 0.2;
  }

  .card-counter .count-numbers {
    position: absolute;
    right: 35px;
    top: 20px;
    font-size: 32px;
    display: block;
  }

  .card-counter .count-name {
    position: absolute;
    right: 35px;
    top: 65px;
    font-style: italic;
    text-transform: capitalize;
    opacity: 0.5;
  }
  .card-counter {
    background-color: gold;
  }
`;
export default class FarmManagement extends Component {
  state = {
    path: "/img/",
    name: "",
    nameUp: "",
    deskripsi: "",
    deskripsiUp: "",
    jenisUp: "",
    warnaUp: "",
    jenis_kelaminUp: "",
    tanggalUp: "",
    hargaUp: "",
    peternak: "",
    statusUp: "",
    jenis: "",
    warna: "",
    jenis_kelamin: "",
    tanggal: "",
    harga: "",
    data: [],
    betina: [],
    jantan: [],
    idUp: "",
    id: 0,
    image1: "",
    image1Up: "",
    image2: "",
    image2Up: "",
    image3: "",
    image3Up: "",
    idbetina: "",
    idjantan: "",
    message: null,
    intervalIsSet: false,
    idToDelete: null,
    idToUpdate: null,
    objectToUpdate: null,
  };
  // when component mounts, first thing it does is fetch all existing data in our db
  // then we incorporate a polling logic so that we can easily see if our db has
  // changed and implement those changes into our UI
  componentDidMount = async () => {
    this.setState({ isLoading: true });
    await api.getAllBirds().then((bird) => {
      this.setState({
        data: bird.data.data,
      });
    });
    var s = 0;
    var t = 0;
    this.state.data.map((dat) => (dat.status == 1 ? (s = s + 1) : (t = t + 1)));
    document.getElementById("stock").innerHTML = s;
    document.getElementById("jual").innerHTML = t;
    await api2.getBetina().then((birdF) => {
      this.setState({
        betina: birdF.data.data,
      });
    });
    await api2.getJantan().then((birdM) => {
      this.setState({
        jantan: birdM.data.data,
      });
    });
  };
  componentWillUnmount() {
    if (this.state.intervalIsSet) {
      clearInterval(this.state.intervalIsSet);
      this.setState({ intervalIsSet: null });
    }
  }

  getDataFromDb = () => {
    api.getAllBirds().then((bird) => {
      this.setState({
        data: bird.data.data,
      });
    });
    var s = 0;
    var t = 0;
    this.state.data.map((dat) => (dat.status == 1 ? (s = s + 1) : (t = t + 1)));
    if (s > 0 || t > 0) {
      document.getElementById("stock").innerHTML = s;
      document.getElementById("jual").innerHTML = t;
    }
  };

  onChangeCheck({ target }) {
    api.getBirdByName(target.value).then((bird) => {
      if (!bird.data.success) {
        this.setState({
          [target.name]: target.value,
        });
        document.getElementById("labelName").innerHTML =
          "ID burung dapat digunakan";
      } else {
        this.setState({
          [target.name]: "",
        });
        document.getElementById("labelName").innerHTML =
          "ID burung sudah digunakan";
      }
    });
  }

  onChange({ target }) {
    this.setState({
      [target.name]: target.value,
    });
  }

  searchBird({ target }) {
    // Declare variables
    var filter, table, tr, td, i, j, txtValue, temp;
    filter = target.value.toUpperCase();
    table = document.getElementById("listBirds");
    tr = table.getElementsByTagName("tr");

    // Loop through all table rows, and hide those who don't match the search query
    for (i = 0; i < tr.length; i++) {
      var c = 0;
      td = tr[i].getElementsByTagName("td");
      for (j = 0; j < td.length; j++) {
        temp = td[j];
        if (temp) {
          txtValue = temp.textContent || temp.innerText;
          if (txtValue.toUpperCase().indexOf(filter) > -1) {
            c = 1;
          }
        }
      }
      if (c > 0) {
        tr[i].style.display = "";
      } else {
        tr[i].style.display = "none";
      }
    }
  }

  addBird = async (e) => {
    e.preventDefault();

    const payload = {
      name: this.state.name,
      deskripsi: this.state.deskripsi,
      jenis: this.state.jenis,
      warna: this.state.warna,

      jenis_kelamin: this.state.jenis_kelamin,
      tanggal: this.state.tanggal,
      harga: this.state.harga,
      image1: this.state.image1,
      image2: this.state.image2,
      image3: this.state.image3,
    };
    if (
      payload.name &&
      payload.deskripsi &&
      payload.jenis &&
      payload.warna &&
      payload.jenis_kelamin &&
      payload.tanggal &&
      payload.image1 &&
      payload.image2 &&
      payload.image3
    ) {
      await api.insertBird(payload).then((res) => {
        window.alert(`Bird inserted successfully`);
        this.getDataFromDb();
        this.setState({
          name: "",
          deskripsi: "",
          jenis: "",
          warna: "",
          jenis_kelamin: "",
          tanggal: "",
          harga: "",
          image1: "",
          image2: "",
          image3: "",
        });

        document.getElementById("outputimage1").src = null;
        document.getElementById("outputimage2").src = null;
        document.getElementById("outputimage3").src = null;
      });
    } else window.alert(`Mohon isi form dengan lengkap`);

    //registerburung(burungData);
  };
  updateData = async (e) => {
    e.preventDefault();

    const payload = {
      name: this.state.nameUp,
      deskripsi: this.state.deskripsiUp,
      jenis: this.state.jenisUp,
      warna: this.state.warnaUp,
      jenis_kelamin: this.state.jenis_kelaminUp,
      tanggal: this.state.tanggalUp,
      harga: this.state.hargaUp,
      status: this.state.statusUp,
      image1: this.state.image1Up,
      image2: this.state.image2Up,
      image3: this.state.image3Up,
    };
    if (
      payload.name &&
      payload.deskripsi &&
      payload.jenis &&
      payload.warna &&
      payload.jenis_kelamin &&
      payload.tanggal &&
      payload.image1 &&
      payload.image2 &&
      payload.image3
    ) {
      await api.updateBirdById(this.state.idUp, payload).then((res) => {
        window.alert(`Bird updated successfully`);
        this.getDataFromDb();
      });
    } else window.alert(`Mohon isi form dengan lengkap`);
    //registerburung(burungData);
  };
  deleteData = async () => {
    await api.deleteBirdById(this.state.idUp).then((res) => {
      window.alert(`Bird deleted successfully`);
      this.getDataFromDb();
    });
    //registerburung(burungData);
  };

  imageVal = (gender, filter) => {
    for (var i = 0; i <= gender.length; i++) {
      if (gender[0].name == filter) {
        return gender[i];
      }
    }
  };

  addBreeding = async (e) => {
    e.preventDefault();
    var imageb = "",
      imagej = "";
    if (this.state.idbetina && this.state.idjantan) {
      imageb = this.imageVal(this.state.betina, this.state.idbetina);
      imagej = this.imageVal(this.state.jantan, this.state.idjantan);
    }
    const payload = {
      betina: this.state.idbetina,
      jantan: this.state.idjantan,
      imagebetina: imageb.image1,
      imagejantan: imagej.image1,
    };
    if (
      payload.betina &&
      payload.jantan &&
      payload.imagebetina &&
      payload.imagejantan
    ) {
      await api2.insertBreeding(payload).then((res) => {
        window.alert(`Breeding inserted successfully`);
        this.setState({
          idbetina: "",
          idjantan: "",
        });
      });
      api2.updateBirdById(imageb._id).then((res) => {});
      api2.updateBirdById(imagej._id).then((res) => {
        this.getDataFromDb();
      });
    } else window.alert(`Mohon isi form dengan lengkap`);

    //registerburung(burungData);
  };

  preview = async ({ target }) => {
    var output = document.getElementById("output" + target.id);
    output.src = URL.createObjectURL(target.files[0]);
  };

  uploadImage = async ({ target }) => {
    var image = document.getElementById(target.name).files[0];
    var formdata = new FormData();
    formdata.append("files", image, image.name);
    await api.upload(formdata).then((res) => {
      if (res.data.success) {
        window.alert("Gambar " + target.name + " Berhasil di upload");
        this.setState({
          [target.name]: res.data.data,
        });
      } else {
        window.alert(res.data.data);
      }
    });
  };

  render() {
    const { data } = this.state;
    const stat = ["Terjual", "Stok"];
    const { betina } = this.state;
    const { jantan } = this.state;
    const age = new Date();

    return (
      <Container>
        <div className="jumbotron jumbotron-fluid">
          <div className="container">
            <h1 className="display-4">Farm Management</h1>
            <p className="lead">
              Farm management digunakan untuk melihat dan menambahkan data
              burung kenari.
            </p>
            <button
              type="button"
              className="btn btn-success"
              data-toggle="modal"
              data-target=".bd-example-modal-lg-register"
            >
              Register Burung
            </button>
            {"          "}
            <button
              type="button"
              className="btn btn-primary"
              data-toggle="modal"
              data-target=".bd-example-modal-lg-parent"
            >
              Register Parent
            </button>

            {/* tambah parent */}
            <form id="addForm">
              <div
